import { Component, OnInit } from '@angular/core';
import { Product } from '../../interfaces/product';
import { CommonModule } from '@angular/common';
import { RouterLink } from '@angular/router';
import { ProductService } from '../../services/product.service';
import { ProgressBarComponent } from '../../shared/progress-bar/progress-bar.component';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-list-products',
  standalone: true,
  imports: [CommonModule, RouterLink, ProgressBarComponent],
  templateUrl: './list-products.component.html',
  styleUrls: ['./list-products.component.css']
})
export class ListProductsComponent implements OnInit{
  listProducts :Product[] = []
  loading: boolean = false;

  constructor(private _productService: ProductService, private toastr: ToastrService){}

  ngOnInit(): void{
    this.getListProducts();
  }

  getListProducts(){
    this.loading = true;
    this._productService.getListProducts().subscribe((data)=>{
      this.listProducts = data;
      this.loading = false;
    })
  }

  deleteProduct(id: number){
    this.loading = true;
    this._productService.deleteProduct(id).subscribe(()=>{      
      this.getListProducts();
      this.toastr.warning("El producto fue eliminado con exito", "Producto eliminado");
    })
  }
  
}
<app-progress-bar *ngIf="loading"></app-progress-bar>
<div *ngIf="!loading" class="container" style="margin-left: 40px;">
    <div class="title">
        <h4>Listado de productos</h4>  
        <button type="button" class="btn btn-success" routerLink="/add">Agregar producto</button>
    </div>    
    <table *ngIf="listProducts.length>0" class="table table-hover table-white-font">
        <thead>
          <tr>
            <th scope="col">Nombre</th>
            <th scope="col">Descripcion</th>
            <th scope="col">Precio</th>
            <th scope="col">Stock</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let item of listProducts" class="table-active">
            <td>{{item.name}}</td>
            <td>{{item.description}}</td>
            <td>{{item.price | currency:'EUR': 'symbol':'1.2-2'}}</td>
            <td>{{item.stock}}</td>
            <td>
                <button type="button" class="btn btn-info btn-sm" routerLink="/edit/{{item.id}}">Editar</button>
                <button (click)="deleteProduct(item.id!)" type="button" class="btn btn-danger btn-sm ms-2">Eliminar</button>
            </td>
          </tr>
          
        </tbody>
      </table>
</div>
