import { CommonModule } from '@angular/common';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, ReactiveFormsModule, Validators } from '@angular/forms';
import { Product } from '../../interfaces/product';
import { ProductService } from '../../services/product.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-add-edit-product',
  standalone: true,
  imports: [CommonModule, ReactiveFormsModule],
  templateUrl: './add-edit-product.component.html',
  styleUrls: ['./add-edit-product.component.css']
})
export class AddEditProductComponent {
  forms: FormGroup;

  constructor(private fb: FormBuilder, private _productService: ProductService, private toastr: ToastrService){
    this.forms = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      price: [null, Validators.required],
      stock: [null, Validators.required],
    })
  }

  addProduct(){
    
    const product: Product = {
      name: this.forms.value.name,
      description: this.forms.value.description,
      price: this.forms.value.price,
      stock: this.forms.value.stock
    }

    this._productService.saveProduct(product).subscribe(()=>{
      console.log("Producto añadido.")
      this.toastr.success("Producto añadido correctamente", "Producto añadido");
    });
    
  }
}
<div class="row mt-1">
    <div class="col-lg-4 offset-lg-4">
        <div class="card text-white bg-secondary mb-3" style="max-width: 20rem;">
            <div class="card-header text-center">Header</div>
            <div class="card-body">
                <form [formGroup]="forms" (ngSubmit)="addProduct()">
                    <input formControlName="name" type="text" class="form-control" placeholder="Ingrese nombre">
                    <span *ngIf="forms.get('name')?.hasError('required') && forms.get('name')?.touched">
                        El campo nombre es <strong>requerido</strong>
                    </span>
                    <input formControlName="description" type="text" class="form-control mt-2" placeholder="Ingrese descripcion">
                    <span *ngIf="forms.get('description')?.hasError('required') && forms.get('description')?.touched">
                        El campo descripcion es <strong>requerido</strong>
                    </span>
                    <input formControlName="price" type="number" class="form-control mt-2" placeholder="Ingrese precio">
                    <span *ngIf="forms.get('price')?.hasError('required') && forms.get('price')?.touched">
                        El campo precio es <strong>requerido</strong>
                    </span>
                    <input formControlName="stock" type="number" class="form-control mt-2" placeholder="Ingrese stock">
                    <span *ngIf="forms.get('stock')?.hasError('required') && forms.get('stock')?.touched">
                        El campo stock es <strong>requerido</strong>
                    </span> 
                    <div class="row">
                        <div class="col-lg-6">
                            <button type="button" class="btn btn-secondary mt-2" routerLink="/">Cancelar</button>
                        </div>
                        <div class="col-lg-6">
                            <button [disabled]="forms.invalid" type="submit" class="btn btn-primary mt-2">Guardar</button>
                        </div>
                    </div>
                </form>
            </div>
        </div>
    </div>
</div>
